import React, { useState } from "react";
import PropTypes from "prop-types";
import { useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";

const AddProduct = ({ onAdd }) => {
    let history = useHistory();
    const [ValueInput, setValueInput] = useState({});
    const onHandleChange = (e) => {
        const { name, image, price, value } = e.target;
        setValueInput({
            ...ValueInput,
            [name]: e.target.value,
            [image]: e.target.value,
            [price]: e.target.value,
        });
    };
    const onHandleSubmit = (e) => {
        e.preventDefault();
        onAdd(ValueInput);
        history.push("/admin/products");
    };
    const { register, handleSubmit, watch, errors } = useForm();
    return (
        <div className="content-wrapper">
            <h1 classname="h3 mb-2 text-gray-800">Thêm Sản Phẩm</h1>
            <div classname="card shadow mb-4">
                <div classname="card-body">
                    <form onSubmit={onHandleSubmit}>
                        <div className="form-group">
                            <label htmlFor="inputProductName">Tên Sản Phẩm</label>
                            <input
                                name="name"
                                type="text"
                                className="form-control"
                                id="inputProductName"
                                onChange={onHandleChange}
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="inputProductImage">Ảnh sản phẩm</label>
                            <input
                                name="image"
                                type="link"
                                className="form-control"
                                id="inputProductImage"
                                onChange={onHandleChange} />
                        </div>
                        <div className="form-group">
                            <label htmlFor="inputProductPrice">Gía sản phẩm</label>
                            <input
                                name="price"
                                type="text"
                                className="form-control"
                                id="inputProductPrice"
                                onChange={onHandleChange} />
                        </div>
                        <button
                            type="submit"
                            className="btn btn-primary"
                            onClick={onHandleSubmit}
                        >
                            Submit
                </button>
                    </form>


                </div>
            </div>
        </div>
    );
};

export default AddProduct;
<form>
    <div className="form-group">
        <label htmlFor="inputProductName">Tên Sản Phẩm</label>
        <input name="name" type="text" className="form-control"
            id="inputProductName" onChange={onHandleChange}
            ref={register({ required: true, minLength: 5 })} />
        {errors.name && <span style={{ color: "red" }}>Tên sản phẩm không được để trống, không dưới năm ký tự</span>}
    </div>
    <div className="form-group">
        <label htmlFor="inputProducImage">Ảnh Sản Phẩm</label>
        <input name="image" type="text" className="form-control"
            id="inputProductImage" onChange={onHandleChange}
            ref={register({ required: true })} />
        {errors.image && <span style={{ color: "red" }}>Ảnh sản phẩm không được để trống</span>}
    </div>
    <div className="form-group">
        <label htmlFor="inputProductPrice">Giá Sản Phẩm</label>
        <input name="price" type="text" className="form-control"
            id="inputProductPrice" onChange={onHandleChange}
            ref={register({ required: true, min: 0 })} />
        {errors.price && <span style={{ color: "red" }}>Gía sản phẩm không được để trống, giá sản phẩm phải lớn hơn không</span>}
    </div>
    <button onClick={handleSubmit(onSubmit)} type="submit" className="btn btn-primary">Submit</button>
</form>
