import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import makeRequest from '../../../../api/axiosHttp';
import React, { useState, useEffect } from 'react';
import Swal from 'sweetalert2'

const ProductsManager = ({ onRemove, products }) => {


    // const [products, setProducts] = useState([]);

    // useEffect(() => {        
    //     const getProducts = async () => {
    //       try {
    //         let url = 'http://localhost:3004/products';            
    //         const {data} =  await makeRequest(url);
    //         setProducts(data);

    //       } catch (error) {
    //         console.log('failed to request API: ', error)
    //       }
    //     }
    //     getProducts();
    // });




    const removeHandle = (id) => {

        Swal.fire({
            title: 'Bạn có chắc chắn muốn xóa?',
            text: "You won't be able to revert this!",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Yes, delete it!'
        }).then((result) => {
            if (result.value) {
                onRemove(id)
                Swal.fire(
                    'Deleted!',
                    'Your file has been deleted.',
                    'success'
                )
            }
        })
        // onRemove(id);
        console.log(id)

    }

    const [filter, setFilter] = useState("")

    return (
        <div>

            <div className="card shadow mb-4">
                <div className="card-header py-3">
                    <Link to="/admin/addproducts" className="btn btn-outline-primary"> Thêm sản phẩm</Link>
                    <form className="d-none d-sm-inline-block form-inline mr-auto ml-md-3 my-2 my-md-0 mw-100 navbar-search">
                        <div className="input-group">
                            <input value={filter} onChange={(e) => setFilter(e.target.value)} type="text" className="form-control bg-light border-0 small" placeholder="Search for..." aria-label="Search" aria-describedby="basic-addon2" />
                            <div className="input-group-append">
                                <button className="btn btn-primary" type="button">
                                    <i className="fas fa-search" />
                                </button>
                            </div>
                        </div>
                    </form>
                </div>
                <div className="card-body">
                    <div className="table-responsive">
                        <table className="table">
                            <thead>
                                <tr>
                                    <th scope="col">ID</th>
                                    <th scope="col">Name</th>
                                    <th scope="col">Image</th>
                                    <th scope="col">Price</th>
                                    <th scope="col">Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {products.map(({ id, name, image, price }, index) => (
                                    <tr key={index}>
                                        <th scope="row">{id}</th>
                                        <td>{name}</td>
                                        <td><img src={image} alt="" width="50" /></td>
                                        <td>{price}</td>
                                        <td>
                                            <Link className='btn btn-outline-primary' to={`/admin/products/edit/${id}`}>Sửa</Link>
                                            <Link className="btn btn-outline-info" to={`/admin/detail-product/${id}`}>Chi tiết</Link>
                                            <button className="btn btn-outline-danger" onClick={() => removeHandle(id)}>Xóa</button>
                                        </td>
                                    </tr>
                                ))}

                            </tbody>
                        </table>

                    </div>
                </div>
            </div>
        </div>
    )
}

ProductsManager.propTypes = {

}

export default ProductsManager
