import React, { useState } from "react";
import PropTypes from "prop-types";
import { useHistory } from "react-router-dom";
import { useForm } from 'react-hook-form';
// function AddProduct({ onAdd }) {
//   const {register, handleSubmit, errors} = useForm();

//   let history = useHistory();
//   const [valueInput, setValueInput] = useState({});
//   function onHandleChange(e) {
//     const { name,image,price, value } = e.target;
//     setValueInput({
//       ...valueInput,
//       [name]: e.target.value,
//       [image]: e.target.value,
//       [price]: e.target.value
//     });
//   }
//   function onSubmit(e) {
//     e.preventDefault();
//     onAdd(valueInput);
//     history.push("/admin/products");
//   }


const AddCategory = ({ onAdd }) => {
    const { register, handleSubmit, errors } = useForm();
    let history = useHistory();
    const onHandleSubmit = (data) => {
        onAdd(data);
        history.push("/admin/category");
    };

    return (
        <div class="content-wrapper">
            <section class="content">
                <h3 className="card-title">Thêm danh mục</h3>
                <form onSubmit={handleSubmit(onHandleSubmit)}>

                    <div className="form-group">
                        <label htmlFor="exampleInputEmail1">Tên danh mục</label>
                        <input
                            type="text"
                            name="name"
                            className="form-control"
                            id="inputCategoryName"

                            ref={register({ required: true })}
                            aria-describedby="nameHelp"
                        />
                        <small id="nameHelp" className="form-text text-danger">{errors.name && <span>Tên danh mục không được để trống</span>}</small>

                    </div>
                    <button type="submit" className="btn btn-primary">
                        Submit
          </button>
                </form>
            </section>
        </div>
    );
}

export default AddCategory;
