import React, { useState } from "react";
import PropTypes from "prop-types";
import { useHistory } from "react-router-dom";


const AddProduct = ({ onAdd }) => {
    let history = useHistory();
    const [ValueInput, setValueInput] = useState({});
    const onHandleChange = (e) => {
        const { name, image, price, value } = e.target;
        setValueInput({
            ...ValueInput,
            [name]: e.target.value,
            [image]: e.target.value,
            [price]: e.target.value,
        });
    };
    const onHandleSubmit = (e) => {
        e.preventDefault();
        onAdd(ValueInput);
        history.push("/admin/products");
    };
    return (
        <div className="content-wrapper">
            <h1 classname="h3 mb-2 text-gray-800">Thêm Sản Phẩm</h1>
            <div classname="card shadow mb-4">
                <div classname="card-body">
                    <form onSubmit={onHandleSubmit}>
                        <div className="form-group">
                            <label htmlFor="inputProductName">Tên Sản Phẩm</label>
                            <input
                                name="name"
                                type="text"
                                className="form-control"
                                id="inputProductName"
                                onChange={onHandleChange}
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="inputProductImage">Ảnh sản phẩm</label>
                            <input
                                name="image"
                                type="link"
                                className="form-control"
                                id="inputProductImage"
                                onChange={onHandleChange} />
                        </div>
                        <div className="form-group">
                            <label htmlFor="inputProductPrice">Gía sản phẩm</label>
                            <input
                                name="price"
                                type="text"
                                className="form-control"
                                id="inputProductPrice"
                                onChange={onHandleChange} />
                        </div>
                        <button
                            type="submit"
                            className="btn btn-primary"
                            onClick={onHandleSubmit}
                        >
                            Submit
                </button>
                    </form>


                </div>
            </div>
        </div>
    );
};

export default AddProduct;
